mapper.select:
SELECT * FROM ( SELECT p.*, rownum AS rnum FROM ( SELECT p.post_id, p.board_id, b.board_name, p.title, p.content, p.author_id, e.name AS author_name, p.created_at, p.updated_at, p.pinned, p.view_cnt FROM posts p LEFT JOIN board b ON p.board_id = b.board_id LEFT JOIN emp e ON p.author_id = e.user_id WHERE 1=1
AND p.board_id = #{board_id}
AND p.title LIKE '%' || #{searchKeyword} || '%'
AND e.name LIKE '%' || #{searchKeyword} || '%'
ORDER BY created_at ) p WHERE rownum <= #{endRow} ) WHERE rnum >= #{startRow}
